INSERT INTO `event_navi_comment` VALUES (/*comment_id*/10045004, /*where_type*/54, /*character_id*/190801, /*face_type*/2, /*character_name*/"花凜", /*description*/"能夠領取獎勵了！\n請檢查看看", /*voice_id*/4, /*start_time*/"2020/11/17 12:00:00", /*end_time*/"2020/11/27 15:59:59", /*pos_x*/-416.5, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/2, /*event_id*/10045);
INSERT INTO `event_navi_comment` VALUES (/*comment_id*/10045005, /*where_type*/57, /*character_id*/190801, /*face_type*/1, /*character_name*/"花凜", /*description*/"參加活動辛苦了！\n各位活躍的樣子\n我們始終非常感謝", /*voice_id*/5, /*start_time*/"2020/11/17 12:00:00", /*end_time*/"2020/11/27 15:59:59", /*pos_x*/-416.5, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/10045);
