INSERT INTO `event_navi_comment` VALUES (/*comment_id*/10045007, /*where_type*/55, /*character_id*/190801, /*face_type*/2, /*character_name*/"花凜", /*description*/"辛苦了！\n在這裡，能夠以討伐證\n來換取獎勵哦！", /*voice_id*/2, /*start_time*/"2020/11/17 12:00:00", /*end_time*/"2020/11/27 15:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/1.216, /*change_face_type*/1, /*event_id*/10045);
